import UIKit
import Foundation
import Alamofire

protocol MovieListWorkerProtocol {
    func fetchMovies(completionHandler:@escaping (() throws -> MovieResponseModel) -> Void)
}

class MovieWorker {
    
    var worker: MovieListWorkerProtocol
    
    init(_ worker: MovieListWorkerProtocol){
        self.worker = worker
    }
    
    func fetchMovies(completionHandler: @escaping ((_ response: MovieResponseModel, Error?) -> Void)) {
        self.worker.fetchMovies( completionHandler:{ (result: () throws -> MovieResponseModel) -> Void in
            do {
                let result = try result()
                NSLog("Movies downloaded!")
                completionHandler(result,nil)
            }catch let error{
                NSLog("Error trying to get movies in network \(error.localizedDescription)")
                print(error)
            }

        })
    }
}
